Ôªønamespace FlexCore.Caching.Common.Handlers;

using FlexCore.Caching.Common.Exceptions;
using System;

/// <summary>
/// Classe statica per la gestione delle eccezioni relative alla cache.
/// </summary>
public static class CacheExceptionHandler
{
    /// <summary>
    /// Gestisce le eccezioni durante le operazioni di cache.
    /// </summary>
    public static void HandleException(Exception ex, string operation)
    {
        throw ex switch
        {
            RedisCacheException => new CacheException($"Errore Redis durante {operation}", ex),
            MemoryCacheException => new CacheException($"Errore di memoria cache durante {operation}", ex),
            _ => new CacheException($"Errore durante l'operazione di cache: {operation}", ex)
        };
    }
}

Ôªønamespace FlexCore.Caching.Common.Validators;

using System;

/// <summary>
/// Classe statica per la validazione delle chiavi di cache.
/// </summary>
public static class CacheKeyValidator
{
    /// <summary>
    /// Verifica che la chiave della cache non sia nulla, vuota o composta solo da spazi bianchi.
    /// </summary>
    /// <param name="key">Chiave da validare.</param>
    public static void ValidateKey(string key)
    {
        if (string.IsNullOrWhiteSpace(key))
            throw new ArgumentException("La chiave non pu√≤ essere nulla o composta solo da spazi vuoti.", nameof(key));
    }
}

Ôªønamespace FlexCore.Caching.Core;

using FlexCore.Caching.Common.Handlers;
using FlexCore.Caching.Common.Validators;
using FlexCore.Caching.Interfaces;
using System;

/// <summary>
/// Classe base astratta per la gestione della cache.
/// </summary>
public abstract class BaseCacheManager : ICacheService
{
    /// <summary>
    /// Ottiene un valore dalla cache.
    /// </summary>
    /// <typeparam name="T">Tipo del valore da ottenere.</typeparam>
    /// <param name="key">Chiave del valore.</param>
    /// <returns>Il valore associato alla chiave.</returns>
    public abstract T Get<T>(string key);

    /// <summary>
    /// Imposta un valore nella cache.
    /// </summary>
    /// <typeparam name="T">Tipo del valore da impostare.</typeparam>
    /// <param name="key">Chiave del valore.</param>
    /// <param name="value">Valore da impostare.</param>
    /// <param name="expiration">Durata della cache.</param>
    public abstract void Set<T>(string key, T value, TimeSpan expiration);

    /// <summary>
    /// Rimuove un valore dalla cache.
    /// </summary>
    /// <param name="key">Chiave del valore da rimuovere.</param>
    public abstract void Remove(string key);

    /// <summary>
    /// Verifica se una chiave esiste nella cache.
    /// </summary>
    /// <param name="key">Chiave da verificare.</param>
    /// <returns>True se la chiave esiste, altrimenti false.</returns>
    public abstract bool Exists(string key);

    /// <summary>
    /// Valida la chiave della cache.
    /// </summary>
    /// <param name="key">Chiave da validare.</param>
    protected static void ValidateKey(string key) => CacheKeyValidator.ValidateKey(key);

    /// <summary>
    /// Gestisce le eccezioni durante le operazioni di cache.
    /// </summary>
    /// <param name="ex">Eccezione.</param>
    /// <param name="operation">Operazione che ha generato l'eccezione.</param>
    protected static void HandleException(Exception ex, string operation) => CacheExceptionHandler.HandleException(ex, operation);
}

Ôªønamespace FlexCore.Caching.Factory;

using FlexCore.Caching.Interfaces;
using System;
using System.Collections.Generic;

/// <summary>
/// Factory per la creazione e registrazione dinamica dei provider di cache.
/// </summary>
public class CacheProviderFactory : ICacheFactory
{
    private readonly Dictionary<string, Func<ICacheProvider>> _providers = new();

    /// <summary>
    /// Registra un provider di cache con un nome specifico.
    /// </summary>
    /// <param name="name">Nome del provider (es. "MemoryCache", "Redis").</param>
    /// <param name="providerFactory">Funzione che crea un'istanza del provider.</param>
    public void RegisterProvider(string name, Func<ICacheProvider> providerFactory)
    {
        if (string.IsNullOrWhiteSpace(name))
            throw new ArgumentException("Il nome del provider non pu√≤ essere vuoto.", nameof(name));

        if (providerFactory == null)
            throw new ArgumentNullException(nameof(providerFactory), "La factory del provider non pu√≤ essere nulla.");

        _providers[name] = providerFactory;
    }

    /// <summary>
    /// Crea un'istanza di un provider di cache in base al nome.
    /// </summary>
    /// <param name="providerName">Nome del provider da creare.</param>
    /// <returns>Un'istanza del provider di cache.</returns>
    public ICacheProvider CreateProvider(string providerName)
    {
        if (_providers.TryGetValue(providerName, out var providerFactory))
        {
            return providerFactory();
        }
        throw new NotSupportedException($"Provider '{providerName}' non supportato.");
    }
}

Ôªønamespace FlexCore.Caching.Factory;

using Microsoft.Extensions.DependencyInjection;
using FlexCore.Caching.Interfaces;

/// <summary>
/// Estensioni per la configurazione del provider di cache.
/// </summary>
public static class CacheServiceExtensions
{
    /// <summary>
    /// Registra un provider di cache in base al nome specificato.
    /// </summary>
    /// <param name="services">La collection di servizi DI.</param>
    /// <param name="providerName">Il nome del provider di cache.</param>
    /// <returns>IServiceCollection con il provider registrato.</returns>
    public static IServiceCollection AddCacheProvider(this IServiceCollection services, string providerName)
    {
        services.AddSingleton<ICacheFactory>(sp => new CacheProviderFactory());
        services.AddSingleton<ICacheProvider>(sp => sp.GetRequiredService<ICacheFactory>().CreateProvider(providerName));
        return services;
    }
}

