Ôªønamespace FlexCore.Core.Configuration.Validators;

using System;

/// <summary>
/// Classe statica per la validazione degli input di configurazione.
/// </summary>
public static class ConfigurationValidator
{
    /// <summary>
    /// Verifica che la chiave di configurazione non sia nulla o vuota.
    /// </summary>
    /// <param name="key">La chiave da validare.</param>
    /// <exception cref="ArgumentNullException">Se la chiave √® null</exception>
    /// <exception cref="ArgumentException">Se la chiave √® vuota o spazi bianchi</exception>
    public static void ValidateKey(string key)
    {
        if (key is null)
            throw new ArgumentNullException(nameof(key), "La chiave non pu√≤ essere nulla.");

        if (string.IsNullOrWhiteSpace(key))
            throw new ArgumentException("La chiave non pu√≤ essere vuota o contenere solo spazi.", nameof(key));
    }
}

Ôªøusing System;
using System.Collections.Generic;
using System.Linq;

namespace FlexCore.Core.Utilities
{
    /// <summary>
    /// Classe statica che fornisce metodi per la validazione e la risoluzione delle dipendenze tra progetti.
    /// </summary>
    public static class DependencyValidator
    {
        /// <summary>
        /// Valida le dipendenze tra i progetti per rilevare eventuali dipendenze circolari.
        /// </summary>
        /// <param name="projectDependencies">Un dizionario contenente i progetti come chiavi e le rispettive dipendenze come valori.</param>
        /// <exception cref="InvalidOperationException">Lanciato quando viene rilevata una dipendenza circolare.</exception>


        //public static void ValidateDependencies(Dictionary<string, List<string>> projectDependencies)
        //{
        //    foreach (var project in projectDependencies.Keys)
        //    {
        //        var dependencies = projectDependencies[project];
        //        // Controlla se un progetto dipende da se stesso (dipendenza circolare)
        //        if (dependencies.Contains(project))
        //        {
        //            throw new InvalidOperationException($"Dipendenza circolare rilevata nel progetto: {project}");
        //        }
        //    }
        //}
        public static void ValidateDependencies(Dictionary<string, List<string>> projectDependencies)
        {
            var visited = new HashSet<string>();
            var stack = new HashSet<string>();

            void Visit(string project)
            {
                if (stack.Contains(project))
                    throw new InvalidOperationException($"Dipendenza circolare rilevata: {project}");

                if (!visited.Contains(project))
                {
                    visited.Add(project);
                    stack.Add(project);

                    foreach (var dependency in projectDependencies.GetValueOrDefault(project, new List<string>()))
                    {
                        Visit(dependency);
                    }

                    stack.Remove(project);
                }
            }

            foreach (var project in projectDependencies.Keys)
            {
                Visit(project);
            }
        }


        /// <summary>
        /// Risolve le dipendenze circolari tra i progetti, se presenti.
        /// </summary>
        /// <param name="projectDependencies">Un dizionario contenente i progetti come chiavi e le rispettive dipendenze come valori.</param>
        public static void ResolveCircularDependencies(Dictionary<string, List<string>> projectDependencies)
        {
            var resolved = new HashSet<string>(); // Per tenere traccia dei progetti risolti
            foreach (var project in projectDependencies.Keys)
            {
                // Se il progetto non √® ancora stato risolto, risolvi le sue dipendenze
                if (!resolved.Contains(project))
                {
                    ResolveDependencies(project, projectDependencies, resolved, new HashSet<string>());
                }
            }
        }

        /// <summary>
        /// Risolve ricorsivamente le dipendenze di un singolo progetto.
        /// </summary>
        /// <param name="project">Il progetto per il quale risolvere le dipendenze.</param>
        /// <param name="projectDependencies">Un dizionario contenente i progetti come chiavi e le rispettive dipendenze come valori.</param>
        /// <param name="resolved">Un insieme che tiene traccia dei progetti gi√† risolti.</param>
        /// <param name="visiting">Un insieme che tiene traccia dei progetti attualmente in fase di visita per evitare dipendenze circolari.</param>
        /// <exception cref="InvalidOperationException">Lanciato quando viene rilevata una dipendenza circolare durante la risoluzione delle dipendenze.</exception>
        private static void ResolveDependencies(string project, Dictionary<string, List<string>> projectDependencies, HashSet<string> resolved, HashSet<string> visiting)
        {
            visiting.Add(project); // Aggiungi il progetto all'elenco dei progetti in visita
            foreach (var dependency in projectDependencies[project])
            {
                // Se una dipendenza non √® ancora stata risolta
                if (!resolved.Contains(dependency))
                {
                    // Se la dipendenza √® gi√† in visita, significa che c'√® una dipendenza circolare
                    if (visiting.Contains(dependency))
                    {
                        throw new InvalidOperationException($"Dipendenza circolare rilevata: {project} -> {dependency}");
                    }
                    // Risolvi ricorsivamente la dipendenza
                    ResolveDependencies(dependency, projectDependencies, resolved, visiting);
                }
            }
            resolved.Add(project); // Aggiungi il progetto ai risolti
            visiting.Remove(project); // Rimuovi il progetto dall'elenco dei progetti in visita
        }
    }
}

Ôªønamespace FlexCore.Core.Utilities;

using System;

/// <summary>
/// Classe statica per la gestione generica delle eccezioni.
/// </summary>
public static class ExceptionHandler
{
    /// <summary>
    /// Gestisce le eccezioni durante le operazioni.
    /// </summary>
    /// <param name="ex">L'eccezione catturata.</param>
    /// <param name="operation">L'operazione che ha generato l'eccezione.</param>
    /// <param name="customExceptionFactory">Funzione per creare eccezioni personalizzate.</param>
    /// <exception cref="Exception">Se si verifica un errore durante l'operazione.</exception>
    public static void HandleException(Exception ex, string operation, Func<Exception, string, Exception> customExceptionFactory)
    {
        throw customExceptionFactory(ex, operation);
    }
}

namespace FlexCore.Database.Core;

using FlexCore.Database.Interfaces;
using Microsoft.EntityFrameworkCore;
using System;
using System.Threading.Tasks;

/// <summary>
/// Contesto database principale con gestione delle transazioni.
/// </summary>
public class ApplicationDbContext : DbContext, IUnitOfWork
{
    /// <summary>
    /// Inizializza una nuova istanza della classe <see cref="ApplicationDbContext"/>.
    /// </summary>
    public ApplicationDbContext(DbContextOptions<ApplicationDbContext> options)
        : base(options)
    {
    }

    /// <summary>
    /// Avvia una transazione nel database.
    /// </summary>
    public void BeginTransaction() => Database.BeginTransaction();

    /// <summary>
    /// Conferma la transazione in corso.
    /// </summary>
    public void CommitTransaction()
    {
        if (Database.CurrentTransaction != null)
        {
            Database.CurrentTransaction.Commit();
            Database.CurrentTransaction.Dispose();
        }
    }

    /// <summary>
    /// Annulla la transazione in corso.
    /// </summary>
    public void RollbackTransaction()
    {
        if (Database.CurrentTransaction != null)
        {
            Database.CurrentTransaction.Rollback();
            Database.CurrentTransaction.Dispose();
        }
    }

    /// <summary>
    /// Salva le modifiche nel database in modo asincrono.
    /// </summary>
    public async Task<int> SaveChangesAsync()
        => await base.SaveChangesAsync().ConfigureAwait(false);

    /// <summary>
    /// Rilascia le risorse gestite.
    /// </summary>
    public override void Dispose()
    {
        base.Dispose(); // Eliminazione gestita da EF Core
        GC.SuppressFinalize(this);
    }
}

namespace FlexCore.Database.Core;

using System.Data;
using System.Threading.Tasks;
using FlexCore.Database.Interfaces;
using Microsoft.Data.SqlClient;

/// <summary>
/// Classe astratta base per la creazione di connessioni a database.
/// Fornisce un'implementazione generica per tutti i provider.
/// </summary>
public abstract class DbConnectionFactory : IDbConnectionFactory
{
    /// <summary>
    /// Stringa di connessione al database.
    /// </summary>
    protected readonly string _connectionString;

    /// <summary>
    /// Inizializza una nuova istanza della classe <see cref="DbConnectionFactory"/>.
    /// </summary>
    /// <param name="connectionString">Stringa di connessione al database. Non pu√≤ essere null.</param>
    /// <exception cref="ArgumentNullException">Se <paramref name="connectionString"/> √® null.</exception>
    protected DbConnectionFactory(string connectionString)
    {
        _connectionString = connectionString ?? throw new ArgumentNullException(nameof(connectionString));
    }

    /// <summary>
    /// Crea e restituisce una connessione aperta al database.
    /// </summary>
    /// <returns>Istanza di <see cref="IDbConnection"/> aperta.</returns>
    public abstract IDbConnection CreateConnection();

    /// <summary>
    /// Crea e restituisce una connessione aperta al database in modo asincrono (implementazione predefinita).
    /// </summary>
    /// <returns>Task che restituisce la connessione aperta.</returns>
    public virtual async Task<IDbConnection> CreateConnectionAsync()
    {
        var connection = CreateConnection();
        await ((SqlConnection)connection).OpenAsync();
        return connection;
    }
}

Ôªønamespace FlexCore.Database.Interfaces;

/// <summary>
