namespace FlexCore.Database.Interfaces;

/// <summary>
/// Interfaccia per la gestione dell'unit√† di lavoro (Unit of Work).
/// Definisce i metodi per salvare le modifiche, gestire transazioni e liberare le risorse.
/// </summary>
public interface IUnitOfWork : IDisposable
{
    /// <summary>
    /// Salva tutte le modifiche apportate al contesto di persistenza in modo sincrono.
    /// </summary>
    /// <returns>Il numero di entit√† aggiornate nel database.</returns>
    int SaveChanges();

    /// <summary>
    /// Salva tutte le modifiche apportate al contesto di persistenza in modo asincrono.
    /// </summary>
    /// <returns>Un Task che rappresenta l'operazione asincrona, contenente il numero di entit√† aggiornate.</returns>
    Task<int> SaveChangesAsync();

    /// <summary>
    /// Avvia una nuova transazione.
    /// </summary>
    void BeginTransaction();

    /// <summary>
    /// Conferma la transazione in corso.
    /// </summary>
    void CommitTransaction();

    /// <summary>
    /// Annulla la transazione in corso.
    /// </summary>
    void RollbackTransaction();
}

Ôªønamespace FlexCore.Database.Core;

using FlexCore.Database.Interfaces;
using System.Transactions;
using System.Threading.Tasks;

/// <summary>
/// Gestore delle transazioni.
/// </summary>
public class TransactionManager : ITransactionManager
{
    /// <summary>
    /// Avvia una transazione asincrona.
    /// </summary>
    public async Task BeginTransactionAsync()
    {
        await Task.CompletedTask; // Implementazione di esempio
    }

    /// <summary>
    /// Conferma la transazione corrente.
    /// </summary>
    public async Task CommitTransactionAsync()
    {
        await Task.CompletedTask; // Implementazione di esempio
    }

    /// <summary>
    /// Annulla la transazione corrente.
    /// </summary>
    public async Task RollbackTransactionAsync()
    {
        await Task.CompletedTask; // Implementazione di esempio
    }

    /// <summary>
    /// Avvia una transazione distribuita asincrona.
    /// </summary>
    public async Task BeginDistributedTransactionAsync()
    {
        using (var scope = new TransactionScope(TransactionScopeAsyncFlowOption.Enabled))
        {
            scope.Complete();
            await Task.CompletedTask;
        }
    }
}

Ôªønamespace FlexCore.Database.Core;

using FlexCore.Database.Interfaces;
using System;

/// <summary>
/// Implementazione base del pattern Unit of Work con gestione semplificata delle risorse.
/// </summary>
public abstract class UnitOfWorkBase : IUnitOfWork
{
    private readonly IDataContext _dataContext;
    private bool _disposed;

    /// <summary>
    /// Inizializza una nuova istanza della classe <see cref="UnitOfWorkBase"/>.
    /// </summary>
    /// <param name="dataContext">Contesto di dati associato all'unit√† di lavoro.</param>
    protected UnitOfWorkBase(IDataContext dataContext)
    {
        _dataContext = dataContext ?? throw new ArgumentNullException(nameof(dataContext));
    }

    // Delega tutte le operazioni di transazione al contesto sottostante
    public void BeginTransaction() => _dataContext.BeginTransaction();
    public void CommitTransaction() => _dataContext.CommitTransaction();
    public void RollbackTransaction() => _dataContext.RollbackTransaction();

    // Metodi per il salvataggio delle modifiche
    public int SaveChanges() => _dataContext.SaveChanges();
    public async Task<int> SaveChangesAsync() => await _dataContext.SaveChangesAsync();

    /// <summary>
    /// Rilascia le risorse gestite.
    /// </summary>
    public void Dispose()
    {
        Dispose(true);
        GC.SuppressFinalize(this);
    }

    /// <summary>
    /// Rilascia le risorse gestite e, opzionalmente, quelle non gestite.
    /// </summary>
    /// <param name="disposing">
    /// Se true, rilascia sia le risorse gestite che quelle non gestite; 
    /// se false, rilascia solo le risorse non gestite.
    /// </param>
    protected virtual void Dispose(bool disposing)
    {
        if (!_disposed)
        {
            if (disposing)
            {
                // Elimina esplicitamente il contesto dati (senza cast ridondanti)
                _dataContext.Dispose();
            }
            _disposed = true;
        }
    }
}

Ôªønamespace FlexCore.Database.Factory;

using FlexCore.Database.Interfaces;
using System;
using System.Collections.Generic;

/// <summary>
/// Factory per la creazione dinamica di provider di database basati su nome e stringa di connessione.
/// </summary>
public class DatabaseProviderFactory : IDatabaseProviderFactory
{
    private readonly Dictionary<string, Func<string, IDbConnectionFactory>> _providers = new();

    /// <summary>
    /// Registra un nuovo provider di database.
    /// </summary>
    /// <param name="name">Nome identificativo del provider (es. "SQLServer", "SQLite").</param>
    /// <param name="providerFactory">Factory che crea un'istanza del provider.</param>
    /// <exception cref="ArgumentException">Se <paramref name="name"/> √® vuoto o nullo.</exception>
    /// <exception cref="ArgumentNullException">Se <paramref name="providerFactory"/> √® nullo.</exception>
    public void RegisterProvider(string name, Func<string, IDbConnectionFactory> providerFactory)
    {
        if (string.IsNullOrWhiteSpace(name))
            throw new ArgumentException("Il nome del provider non pu√≤ essere vuoto.", nameof(name));

        _providers[name] = providerFactory ?? throw new ArgumentNullException(nameof(providerFactory));
    }

    /// <summary>
    /// Crea un'istanza del provider specificato.
    /// </summary>
    /// <param name="providerName">Nome del provider registrato.</param>
    /// <param name="connectionString">Stringa di connessione da utilizzare.</param>
    /// <returns>Istanza del provider di database.</returns>
    /// <exception cref="NotSupportedException">Se il provider non √® registrato.</exception>
    public IDbConnectionFactory CreateProvider(string providerName, string connectionString)
    {
        if (_providers.TryGetValue(providerName, out var factory))
        {
            return factory(connectionString);
        }
        throw new NotSupportedException($"Provider '{providerName}' non supportato.");
    }
}

Ôªønamespace FlexCore.Database.Factory.Extensions;

using Microsoft.Extensions.DependencyInjection;
using FlexCore.Database.Factory;
using FlexCore.Database.Interfaces;

/// <summary>
/// Estensioni per la configurazione del provider di database tramite Dependency Injection.
/// </summary>
public static class DatabaseServiceExtensions
{
    /// <summary>
    /// Registra un provider di database nel container DI.
    /// </summary>
    /// <param name="services">Collezione di servizi.</param>
    /// <param name="providerName">Nome del provider (es. "SQLServer").</param>
    /// <param name="connectionString">Stringa di connessione al database.</param>
    /// <returns>La collezione di servizi per il method chaining.</returns>
    public static IServiceCollection AddDatabaseProvider(
        this IServiceCollection services,
        string providerName,
        string connectionString)
    {
        services.AddSingleton<IDatabaseProviderFactory, DatabaseProviderFactory>();
        services.AddSingleton<IDbConnectionFactory>(sp =>
        {
            var factory = sp.GetRequiredService<IDatabaseProviderFactory>();
            return factory.CreateProvider(providerName, connectionString);
        });
        return services;
    }
}

Ôªønamespace FlexCore.Database.MariaDB;

using FlexCore.Database.Core;
using MySqlConnector;
using System;
using System.Data;
using System.Threading.Tasks;

public class MariaDBProvider : DbConnectionFactory
{
    public MariaDBProvider(string connectionString) : base(connectionString)
    {
        if (string.IsNullOrWhiteSpace(connectionString))
            throw new ArgumentNullException(nameof(connectionString));
    }

    public override IDbConnection CreateConnection()
    {
        var connection = new MySqlConnection(_connectionString);
        connection.Open();
        return connection;
    }

    public override async Task<IDbConnection> CreateConnectionAsync()
    {
        var connection = new MySqlConnection(_connectionString);
        await connection.OpenAsync();
        return connection;
    }
}

namespace FlexCore.Database.SQLite;

using FlexCore.Database.Core;
using Microsoft.Data.Sqlite;
using System;
using System.Data;
using System.Threading.Tasks;

/// <summary>
/// Implementazione del provider per SQLite.
/// </summary>
public class SQLiteProvider : DbConnectionFactory
{
    private new readonly string _connectionString;

    /// <summary>
    /// Inizializza una nuova istanza della classe <see cref="SQLiteProvider"/>.
    /// </summary>
    /// <param name="connectionString">Stringa di connessione al database.</param>
    public SQLiteProvider(string connectionString) : base(connectionString)
    {
        if (string.IsNullOrWhiteSpace(connectionString))
            throw new ArgumentNullException(nameof(connectionString), "La stringa di connessione non pu√≤ essere vuota.");

        _connectionString = connectionString;
    }

    /// <summary>
    /// Crea una connessione al database SQLite.
    /// </summary>
    /// <returns>Connessione al database.</returns>
    public override IDbConnection CreateConnection()
    {
        if (string.IsNullOrEmpty(_connectionString))
            throw new InvalidOperationException("La stringa di connessione non √® stata configurata.");

        var connection = new SqliteConnection(_connectionString);
        connection.Open();
        return connection;
    }

    /// <summary>
    /// Crea una connessione al database SQLite in modo asincrono.
    /// </summary>
    /// <returns>Connessione al database.</returns>
    public override async Task<IDbConnection> CreateConnectionAsync()
    {
        if (string.IsNullOrEmpty(_connectionString))
            throw new InvalidOperationException("La stringa di connessione non √® stata configurata.");

        var connection = new SqliteConnection(_connectionString);
        await connection.OpenAsync();
        return connection;
    }
}

