        /// </summary>
        /// <typeparam name="TEvent">Tipo dell'evento</typeparam>
        /// <param name="event">Istanza dell'evento</param>
        Task PublishAsync<TEvent>(TEvent @event) where TEvent : IEvent;
    }
}

