namespace FlexCore.ORM.Core.Implementations;

using FlexCore.ORM.Core.Interfaces;
using System;
using System.Collections.Generic;
using System.Threading.Tasks;

/// <summary>
/// Classe base astratta per i provider ORM.
/// </summary>
public abstract class OrmProviderBase : IOrmProvider
{
    // Flag per indicare se le risorse sono giÔøΩ state rilasciate
    private bool _disposed = false;

    /// <summary>
    /// Inizializza il provider ORM.
    /// </summary>
    public abstract void Initialize();

    /// <summary>
    /// Esegue una query SQL sul database.
    /// </summary>
    /// <param name="query">La query SQL da eseguire.</param>
    public abstract void ExecuteQuery(string query);

    /// <summary>
    /// Recupera un'entitÔøΩ dal database tramite il suo ID.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entitÔøΩ.</typeparam>
    /// <param name="id">L'ID dell'entitÔøΩ da recuperare.</param>
    /// <returns>L'entitÔøΩ trovata o null se non esiste.</returns>
    public abstract Task<T?> GetByIdAsync<T>(Guid id) where T : class;

    /// <summary>
    /// Recupera tutte le entitÔøΩ di un determinato tipo dal database.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entitÔøΩ.</typeparam>
    /// <returns>Una collezione di entitÔøΩ.</returns>
    public abstract Task<IEnumerable<T>> GetAllAsync<T>() where T : class;

    /// <summary>
    /// Aggiunge una nuova entitÔøΩ al database.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entitÔøΩ.</typeparam>
    /// <param name="entity">L'entitÔøΩ da aggiungere.</param>
    public abstract Task AddAsync<T>(T entity) where T : class;

    /// <summary>
    /// Aggiunge una collezione di entitÔøΩ al database.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entitÔøΩ.</typeparam>
    /// <param name="entities">Le entitÔøΩ da aggiungere.</param>
    public abstract Task AddRangeAsync<T>(IEnumerable<T> entities) where T : class;

    /// <summary>
    /// Aggiorna un'entitÔøΩ esistente nel database.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entitÔøΩ.</typeparam>
    /// <param name="entity">L'entitÔøΩ da aggiornare.</param>
    public abstract Task UpdateAsync<T>(T entity) where T : class;

    /// <summary>
    /// Aggiorna una collezione di entitÔøΩ nel database.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entitÔøΩ.</typeparam>
    /// <param name="entities">Le entitÔøΩ da aggiornare.</param>
    public abstract Task UpdateRangeAsync<T>(IEnumerable<T> entities) where T : class;

    /// <summary>
    /// Elimina un'entitÔøΩ dal database.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entitÔøΩ.</typeparam>
    /// <param name="entity">L'entitÔøΩ da eliminare.</param>
    public abstract Task DeleteAsync<T>(T entity) where T : class;

    /// <summary>
    /// Elimina una collezione di entitÔøΩ dal database.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entitÔøΩ.</typeparam>
    /// <param name="entities">Le entitÔøΩ da eliminare.</param>
    public abstract Task DeleteRangeAsync<T>(IEnumerable<T> entities) where T : class;

    /// <summary>
    /// Salva tutte le modifiche apportate al database.
    /// </summary>
    /// <returns>Il numero di entitÔøΩ modificate.</returns>
    public abstract Task<int> SaveChangesAsync();

    /// <summary>
    /// Inizia una nuova transazione asincrona.
    /// </summary>
    public abstract Task BeginTransactionAsync();

    /// <summary>
    /// Conferma la transazione corrente.
    /// </summary>
    public abstract Task CommitTransactionAsync();

    /// <summary>
    /// Annulla la transazione corrente.
    /// </summary>
    public abstract Task RollbackTransactionAsync();

    /// <summary>
    /// Rilascia le risorse gestite e non gestite.
    /// </summary>
    public void Dispose()
    {
        Dispose(true);
        GC.SuppressFinalize(this);
    }

    /// <summary>
    /// Rilascia le risorse gestite e non gestite.
    /// </summary>
    /// <param name="disposing">Indica se rilasciare risorse gestite.</param>
    protected virtual void Dispose(bool disposing)
    {
        if (!_disposed)
        {
            if (disposing)
            {
                // Rilascia le risorse gestite (ad esempio, connessioni al database)
            }

            // Rilascia le risorse non gestite (se presenti)
            _disposed = true;
        }
    }

    /// <summary>
    /// Distruttore per garantire il rilascio delle risorse.
    /// </summary>
    ~OrmProviderBase()
    {
        Dispose(false);
    }
}

Ôªønamespace FlexCore.ORM.Core.Implementations
{
    using System;

    /// <summary>
    /// Classe astratta che fornisce le basi per l'implementazione di un'unit√† di lavoro.
    /// Gestisce la transazione e il salvataggio delle modifiche in un contesto di dati.
    /// </summary>
    public abstract class UnitOfWorkBase : IDisposable
    {
        // Flag per tenere traccia se l'oggetto √® stato gi√† rilasciato.
        protected bool _disposed;

        /// <summary>
        /// Avvia una transazione in modo asincrono.
        /// </summary>
        /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
        public abstract Task BeginTransactionAsync();

        /// <summary>
        /// Commette la transazione in corso in modo asincrono.
        /// </summary>
        /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
        public abstract Task CommitTransactionAsync();

        /// <summary>
        /// Annulla la transazione in corso in modo asincrono.
        /// </summary>
        /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
        public abstract Task RollbackTransactionAsync();

        /// <summary>
        /// Salva le modifiche effettuate nel contesto di dati in modo asincrono.
        /// </summary>
        /// <returns>Un task che rappresenta l'operazione asincrona, restituendo il numero di entit√† salvate.</returns>
        public abstract Task<int> SaveChangesAsync();

        /// <summary>
        /// Esegue la logica di rilascio delle risorse.
        /// Questo metodo √® protetto e deve essere implementato dalle classi derivate.
        /// </summary>
        /// <param name="disposing">Indica se il metodo √® stato chiamato tramite il metodo Dispose o dal finalizzatore.</param>
        protected abstract void Dispose(bool disposing);

        /// <summary>
        /// Rilascia le risorse quando l'oggetto non √® pi√π necessario.
        /// </summary>
        public void Dispose()
        {
            // Non chiamare Dispose pi√π di una volta.
            Dispose(true);
            GC.SuppressFinalize(this);
        }
    }
}

namespace FlexCore.ORM.Core.Interfaces;

using System;
using System.Collections.Generic;
using System.Threading.Tasks;

/// <summary>
/// Interfaccia per le operazioni CRUD generiche su un database.
/// Include metodi per il recupero, l'aggiunta, l'aggiornamento e la cancellazione di entit‚Ä°.
/// Inoltre gestisce le transazioni attraverso i metodi di commit, rollback e salvataggio delle modifiche.
/// </summary>
public interface IOrmProvider : IDisposable
{
    /// <summary>
    /// Recupera un'entit‚Ä° dal database in base all'ID.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit‚Ä°.</typeparam>
    /// <param name="id">L'ID dell'entit‚Ä° da recuperare.</param>
    /// <returns>Un task che restituisce l'entit‚Ä° se trovata, altrimenti null.</returns>
    Task<T?> GetByIdAsync<T>(Guid id) where T : class;

    /// <summary>
    /// Recupera tutte le entit‚Ä° di tipo <typeparamref name="T"/> dal database.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit‚Ä°.</typeparam>
    /// <returns>Un task che restituisce una raccolta di entit‚Ä°.</returns>
    Task<IEnumerable<T>> GetAllAsync<T>() where T : class;

    /// <summary>
    /// Aggiunge una nuova entit‚Ä° al database.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit‚Ä°.</typeparam>
    /// <param name="entity">L'entit‚Ä° da aggiungere.</param>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    Task AddAsync<T>(T entity) where T : class;

    /// <summary>
    /// Aggiunge un intervallo di entit‚Ä° al database.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit‚Ä°.</typeparam>
    /// <param name="entities">Le entit‚Ä° da aggiungere.</param>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    Task AddRangeAsync<T>(IEnumerable<T> entities) where T : class;

    /// <summary>
    /// Aggiorna un'entit‚Ä° esistente nel database.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit‚Ä°.</typeparam>
    /// <param name="entity">L'entit‚Ä° da aggiornare.</param>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    Task UpdateAsync<T>(T entity) where T : class;

    /// <summary>
    /// Aggiorna un intervallo di entit‚Ä° esistenti nel database.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit‚Ä°.</typeparam>
    /// <param name="entities">Le entit‚Ä° da aggiornare.</param>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    Task UpdateRangeAsync<T>(IEnumerable<T> entities) where T : class;

    /// <summary>
    /// Elimina un'entit‚Ä° dal database.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit‚Ä°.</typeparam>
    /// <param name="entity">L'entit‚Ä° da eliminare.</param>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    Task DeleteAsync<T>(T entity) where T : class;

    /// <summary>
    /// Elimina un intervallo di entit‚Ä° dal database.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit‚Ä°.</typeparam>
    /// <param name="entities">Le entit‚Ä° da eliminare.</param>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    Task DeleteRangeAsync<T>(IEnumerable<T> entities) where T : class;

    /// <summary>
    /// Salva le modifiche effettuate nel contesto.
    /// </summary>
    /// <returns>Un task che restituisce il numero di modifiche salvate.</returns>
    Task<int> SaveChangesAsync();

    /// <summary>
    /// Avvia una nuova transazione asincrona.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    Task BeginTransactionAsync();

    /// <summary>
    /// Conferma la transazione attiva asincrona.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    Task CommitTransactionAsync();

    /// <summary>
    /// Annulla la transazione attiva asincrona.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    Task RollbackTransactionAsync();
}

