Ôªønamespace FlexCore.ORM.Providers.ADO;

using System;
using System.Data;
using System.Threading.Tasks;
using FlexCore.ORM.Core.Implementations;

/// <summary>
/// Implementa il pattern UnitOfWork utilizzando ADO.NET per la gestione delle transazioni.
/// Gestisce le operazioni di transazione come commit e rollback per una connessione ADO.NET.
/// </summary>
public class AdoNetUnitOfWork : UnitOfWorkBase
{
    private readonly IDbConnection _connection;
    private IDbTransaction? _transaction = null;

    /// <summary>
    /// Inizializza una nuova istanza di <see cref="AdoNetUnitOfWork"/>.
    /// </summary>
    /// <param name="connection">La connessione al database.</param>
    /// <exception cref="ArgumentNullException">L'argomento <paramref name="connection"/> √® nullo.</exception>
    public AdoNetUnitOfWork(IDbConnection connection)
    {
        _connection = connection ?? throw new ArgumentNullException(nameof(connection));
    }

    /// <summary>
    /// Avvia una nuova transazione sul database.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public override async Task BeginTransactionAsync()
    {
        if (_connection.State != ConnectionState.Open)
            await Task.Run(() => _connection.Open());

        _transaction = await Task.Run(() => _connection.BeginTransaction());
    }

    /// <summary>
    /// Conferma la transazione attiva.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    /// <exception cref="InvalidOperationException">Se non c'√® alcuna transazione attiva.</exception>
    public override async Task CommitTransactionAsync()
    {
        if (_transaction == null)
            throw new InvalidOperationException("Nessuna transazione attiva.");

        await Task.Run(() => _transaction.Commit());
        _transaction = null;
    }

    /// <summary>
    /// Annulla la transazione attiva.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    /// <exception cref="InvalidOperationException">Se non c'√® alcuna transazione attiva.</exception>
    public override async Task RollbackTransactionAsync()
    {
        if (_transaction == null)
            throw new InvalidOperationException("Nessuna transazione attiva.");

        await Task.Run(() => _transaction.Rollback());
        _transaction = null;
    }

    /// <summary>
    /// Salva le modifiche effettuate nel contesto. Non implementa operazioni come Entity Framework, quindi ritorna sempre 0.
    /// </summary>
    /// <returns>Un intero che rappresenta il numero di modifiche salvate.</returns>
    public override Task<int> SaveChangesAsync()
    {
        return Task.FromResult(0); // ADO.NET non gestisce SaveChanges come Entity Framework.
    }

    /// <summary>
    /// Rilascia le risorse utilizzate dalla connessione e dalla transazione.
    /// </summary>
    /// <param name="disposing">Indica se il metodo √® stato chiamato dal metodo Dispose pubblico o dal finalizzatore.</param>
    protected override void Dispose(bool disposing)
    {
        if (!_disposed)
        {
            if (disposing)
            {
                _transaction?.Dispose();
            }

            _connection?.Dispose();
            _disposed = true;
        }
    }
}

using System;
using System.Collections.Generic;
using System.Data;
using System.Data.Common;
using System.Linq;
using System.Threading.Tasks;
using FlexCore.ORM.Core.Interfaces;

namespace FlexCore.ORM.Providers.ADO
{
    public class AdoOrmProvider(IDbConnection connection) : IOrmProvider, IDisposable
    {
        private readonly IDbConnection _connection = connection ?? throw new ArgumentNullException(nameof(connection));
        private IDbTransaction? _transaction = null;

        public async Task<T?> GetByIdAsync<T>(Guid id) where T : class
        {
            using var command = _connection.CreateCommand();
            command.CommandText = $"SELECT * FROM {typeof(T).Name} WHERE Id = @Id";

            var param = command.CreateParameter();
            param.ParameterName = "@Id";
            param.Value = id;
            command.Parameters.Add(param);

            using var reader = await ExecuteReaderAsync(command);
            return await ReadEntityAsync<T>(reader);
        }

        public async Task AddAsync<T>(T entity) where T : class
        {
            var query = GenerateInsertQuery<T>();
            using var command = _connection.CreateCommand();
            command.CommandText = query;

            foreach (var prop in typeof(T).GetProperties())
            {
                var param = command.CreateParameter();
                param.ParameterName = $"@{prop.Name}";
                param.Value = prop.GetValue(entity) ?? DBNull.Value;
                command.Parameters.Add(param);
            }

            await ExecuteNonQueryAsync(command);
        }

        public async Task<IEnumerable<T>> GetAllAsync<T>() where T : class
        {
            using var command = _connection.CreateCommand();
            command.CommandText = $"SELECT * FROM {typeof(T).Name}";

            using var reader = await ExecuteReaderAsync(command);
            var results = new List<T>();

            while (await reader.ReadAsync())
            {
                results.Add(MapToEntity<T>(reader));
            }
            return results;
        }

        public async Task AddRangeAsync<T>(IEnumerable<T> entities) where T : class
        {
            foreach (var entity in entities)
            {
                await AddAsync(entity);
            }
        }

        public async Task UpdateAsync<T>(T entity) where T : class
        {
            var setClause = string.Join(", ",
                typeof(T).GetProperties()
                .Where(p => p.Name != "Id")
                .Select(p => $"{p.Name} = @{p.Name}"));

            using var command = _connection.CreateCommand();
            command.CommandText = $"UPDATE {typeof(T).Name} SET {setClause} WHERE Id = @Id";

            foreach (var prop in typeof(T).GetProperties())
            {
                var param = command.CreateParameter();
                param.ParameterName = $"@{prop.Name}";
                param.Value = prop.GetValue(entity) ?? DBNull.Value;
                command.Parameters.Add(param);
            }

            await ExecuteNonQueryAsync(command);
        }

        public async Task UpdateRangeAsync<T>(IEnumerable<T> entities) where T : class
        {
            foreach (var entity in entities)
            {
                await UpdateAsync(entity);
            }
        }

        public async Task DeleteAsync<T>(T entity) where T : class
        {
            var id = typeof(T).GetProperty("Id")?.GetValue(entity);
            using var command = _connection.CreateCommand();
            command.CommandText = $"DELETE FROM {typeof(T).Name} WHERE Id = @Id";

            var param = command.CreateParameter();
            param.ParameterName = "@Id";
            param.Value = id;
            command.Parameters.Add(param);

            await ExecuteNonQueryAsync(command);
        }

        public async Task DeleteRangeAsync<T>(IEnumerable<T> entities) where T : class
        {
            foreach (var entity in entities)
            {
                await DeleteAsync(entity);
            }
        }

        public Task<int> SaveChangesAsync() => Task.FromResult(0);

        public async Task BeginTransactionAsync()
        {
            if (_connection.State != ConnectionState.Open)
                _connection.Open();

            _transaction = _connection.BeginTransaction();
            await Task.CompletedTask;
        }

        public async Task CommitTransactionAsync()
        {
            _transaction?.Commit();
            _transaction = null;
            await Task.CompletedTask;
        }

        public async Task RollbackTransactionAsync()
        {
            _transaction?.Rollback();
            _transaction = null;
            await Task.CompletedTask;
        }

        private async Task<DbDataReader> ExecuteReaderAsync(IDbCommand command)
        {
            // CA1859: Utilizzo di tipo concreto DbCommand
            if (command is DbCommand dbCommand)
            {
                return await dbCommand.ExecuteReaderAsync();
            }

            // CS0266: Cast esplicito per IDataReader
            return (DbDataReader)await Task.Run(() => command.ExecuteReader());
        }

        private async Task<int> ExecuteNonQueryAsync(IDbCommand command)
        {
            // CA1859: Utilizzo di tipo concreto DbCommand
            if (command is DbCommand dbCommand)
            {
                return await dbCommand.ExecuteNonQueryAsync();
            }

            return await Task.Run(() => command.ExecuteNonQuery());
        }

        // CA1822: Metodo reso static
        private static async Task<T?> ReadEntityAsync<T>(DbDataReader reader) where T : class
        {
            return await reader.ReadAsync() ? MapToEntity<T>(reader) : null;
        }

        // CA1822: Metodo reso static
        private static T MapToEntity<T>(IDataRecord reader) where T : class
        {
            var entity = Activator.CreateInstance<T>();
            for (int i = 0; i < reader.FieldCount; i++)
            {
                var prop = typeof(T).GetProperty(reader.GetName(i));
                prop?.SetValue(entity, reader.IsDBNull(i) ? null : reader.GetValue(i));
            }
            return entity;
        }

        // CA1822: Metodo reso static
        private static string GenerateInsertQuery<T>()
        {
            var properties = typeof(T).GetProperties();
            var columns = string.Join(", ", properties.Select(p => p.Name));
            var parameters = string.Join(", ", properties.Select(p => $"@{p.Name}"));
            return $"INSERT INTO {typeof(T).Name} ({columns}) VALUES ({parameters})";
        }

        public void Dispose()
        {
            _transaction?.Dispose();
            _connection?.Dispose();
            GC.SuppressFinalize(this);
        }
    }
}

Ôªø#nullable enable
using Dapper;
using Dapper.Contrib.Extensions;
using FlexCore.ORM.Core.Interfaces;
using System;
using System.Collections.Generic;
using System.Data;
using System.Linq;
using System.Threading.Tasks;

namespace FlexCore.ORM.Providers.Dapper;

/// <summary>
/// Implementazione del provider ORM basato su Dapper.Contrib.
/// Gestisce la mappatura degli oggetti, le transazioni e le operazioni CRUD.
/// </summary>
public class DapperOrmProvider : IOrmProvider, IDisposable
{
    private readonly IDbConnection _connection;
    private IDbTransaction? _transaction;

    /// <summary>
    /// Inizializza i gestori di tipo per i GUID.
    /// </summary>
    static DapperOrmProvider()
    {
        SqlMapper.RemoveTypeMap(typeof(Guid));
        SqlMapper.AddTypeHandler(new GuidTypeHandler());
    }

    /// <summary>
    /// Inizializza una nuova istanza del provider Dapper.
    /// </summary>
    /// <param name="connection">Connessione al database gi√† configurata</param>
    /// <exception cref="ArgumentNullException">Se la connessione √® null</exception>
    public DapperOrmProvider(IDbConnection connection)
    {
        _connection = connection ?? throw new ArgumentNullException(nameof(connection));
    }

    /// <summary>
    /// Gestore personalizzato per la conversione GUID/stringa.
    /// </summary>
    public class GuidTypeHandler : SqlMapper.TypeHandler<Guid>
    {
        /// <summary>
        /// Converte una stringa in GUID.
        /// </summary>
        /// <param name="value">Valore da convertire</param>
        /// <returns>Guid parsato</returns>
        /// <exception cref="FormatException">Se il formato non √® valido</exception>
        public override Guid Parse(object value) => Guid.Parse(value.ToString()!);

        /// <summary>
        /// Imposta il parametro SQL con il GUID formattato come stringa.
        /// </summary>
        /// <param name="parameter">Parametro SQL da impostare</param>
        /// <param name="value">Valore GUID da convertire</param>
        public override void SetValue(IDbDataParameter parameter, Guid value)
        {
            parameter.DbType = DbType.String;
            parameter.Value = value.ToString("N");
        }
    }

    /// <summary>
    /// Recupera un'entit√† tramite ID.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit√†</typeparam>
    /// <param name="id">ID univoco dell'entit√†</param>
    /// <returns>Task che restituisce l'entit√† o null</returns>
    public async Task<T?> GetByIdAsync<T>(Guid id) where T : class =>
        await _connection.GetAsync<T>(id);

    /// <summary>
    /// Recupera tutte le entit√† di tipo T.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit√†</typeparam>
    /// <returns>Task che restituisce una collezione di entit√†</returns>
    public async Task<IEnumerable<T>> GetAllAsync<T>() where T : class =>
        await _connection.GetAllAsync<T>();

    /// <summary>
    /// Aggiunge una nuova entit√† al database.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit√†</typeparam>
    /// <param name="entity">Entit√† da aggiungere</param>
    /// <returns>Task completato</returns>
    public async Task AddAsync<T>(T entity) where T : class =>
        await _connection.InsertAsync(entity);

    /// <summary>
    /// Aggiunge una collezione di entit√† al database.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit√†</typeparam>
    /// <param name="entities">Collezione di entit√† da aggiungere</param>
    /// <returns>Task completato</returns>
    public async Task AddRangeAsync<T>(IEnumerable<T> entities) where T : class =>
        await _connection.InsertAsync(entities);

    /// <summary>
    /// Aggiorna un'entit√† esistente.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit√†</typeparam>
    /// <param name="entity">Entit√† con i valori aggiornati</param>
    /// <returns>Task completato</returns>
    public async Task UpdateAsync<T>(T entity) where T : class =>
        await _connection.UpdateAsync(entity);

    /// <summary>
    /// Aggiorna una collezione di entit√†.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit√†</typeparam>
    /// <param name="entities">Collezione di entit√† da aggiornare</param>
    /// <returns>Task completato</returns>
    public async Task UpdateRangeAsync<T>(IEnumerable<T> entities) where T : class =>
        await _connection.UpdateAsync(entities);

    /// <summary>
    /// Elimina un'entit√† dal database.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit√†</typeparam>
    /// <param name="entity">Entit√† da eliminare</param>
    /// <returns>Task completato</returns>
    public async Task DeleteAsync<T>(T entity) where T : class =>
        await _connection.DeleteAsync(entity);

    /// <summary>
    /// Elimina una collezione di entit√†.
    /// </summary>
    /// <typeparam name="T">Tipo dell'entit√†</typeparam>
    /// <param name="entities">Collezione di entit√† da eliminare</param>
    /// <returns>Task completato</returns>
    public async Task DeleteRangeAsync<T>(IEnumerable<T> entities) where T : class =>
        await _connection.DeleteAsync(entities);

    /// <summary>
    /// Salva le modifiche (non necessario in Dapper).
    /// </summary>
    /// <returns>Task che restituisce sempre 0</returns>
    public Task<int> SaveChangesAsync() => Task.FromResult(0);

    /// <summary>
    /// Avvia una transazione asincrona.
    /// </summary>
    /// <returns>Task completato</returns>
    /// <exception cref="InvalidOperationException">Se la connessione non √® aperta</exception>
    public async Task BeginTransactionAsync()
    {
        if (_connection.State != ConnectionState.Open)
            throw new InvalidOperationException("La connessione deve essere aperta");

        _transaction = await Task.Run(() => _connection.BeginTransaction());
    }

    /// <summary>
    /// Conferma la transazione corrente.
    /// </summary>
    /// <returns>Task completato</returns>
    /// <exception cref="InvalidOperationException">Se non c'√® una transazione attiva</exception>
    public async Task CommitTransactionAsync()
    {
        if (_transaction == null)
            throw new InvalidOperationException("Nessuna transazione attiva");

        await Task.Run(() => _transaction.Commit());
        _transaction = null;
    }

    /// <summary>
    /// Annulla la transazione corrente.
    /// </summary>
    /// <returns>Task completato</returns>
    /// <exception cref="InvalidOperationException">Se non c'√® una transazione attiva</exception>
    public async Task RollbackTransactionAsync()
    {
        if (_transaction == null)
            throw new InvalidOperationException("Nessuna transazione attiva");

        await Task.Run(() => _transaction.Rollback());
        _transaction = null;
    }

    /// <summary>
    /// Rilascia le risorse gestite (connessione e transazione).
    /// </summary>
    public void Dispose()
    {
        _transaction?.Dispose();
        _connection?.Dispose();
        GC.SuppressFinalize(this);
    }
}

Ôªønamespace FlexCore.ORM.Providers.Dapper;

using System.Data;
using System.Threading.Tasks;
using System;
using FlexCore.ORM.Core.Implementations;

/// <summary>
/// Implementazione del UnitOfWork per Dapper. Gestisce le transazioni e l'accesso al database tramite Dapper.
/// </summary>
public class DapperUnitOfWork : UnitOfWorkBase
{
    // Connessione al database
    private readonly IDbConnection _connection;
    // Transazione associata alla connessione
    private IDbTransaction _transaction;

    /// <summary>
    /// Inizializza una nuova istanza di <see cref="DapperUnitOfWork"/>.
    /// </summary>
    /// <param name="connection">La connessione al database utilizzata da Dapper.</param>
    public DapperUnitOfWork(IDbConnection connection)
    {
        _connection = connection ?? throw new ArgumentNullException(nameof(connection));
    }

    /// <summary>
    /// Avvia una nuova transazione asincrona.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public override async Task BeginTransactionAsync()
    {
        // Verifica se la connessione √® aperta, altrimenti la apre
        if (_connection.State != ConnectionState.Open)
            _connection.Open();

        // Inizia una nuova transazione
        _transaction = _connection.BeginTransaction();
        await Task.CompletedTask;
    }

    /// <summary>
    /// Commette la transazione corrente.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public override async Task CommitTransactionAsync()
    {
        // Commette la transazione se non √® null
        _transaction?.Commit();
        _transaction = null;
        await Task.CompletedTask;
    }

    /// <summary>
    /// Annulla la transazione corrente.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public override async Task RollbackTransactionAsync()
    {
        // Annulla la transazione se non √® null
        _transaction?.Rollback();
        _transaction = null;
        await Task.CompletedTask;
    }

    /// <summary>
    /// Metodo per salvare le modifiche. Dapper non gestisce SaveChanges come EF Core, quindi restituisce sempre 0.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona. Restituisce 0.</returns>
    public override async Task<int> SaveChangesAsync()
    {
        // Dapper non ha una gestione diretta di SaveChanges, quindi restituisce sempre 0
        return await Task.FromResult(0);
    }

    /// <summary>
    /// Rilascia le risorse utilizzate dal UnitOfWork.
    /// </summary>
    /// <param name="disposing">Indica se il metodo √® stato chiamato dal Dispose o dal finalizzatore.</param>
    protected override void Dispose(bool disposing)
    {
        if (!_disposed)
        {
            if (disposing)
            {
                // Rilascia le risorse gestite
                _transaction?.Dispose();
            }

            // Rilascia le risorse non gestite (connessione al database)
            _connection?.Dispose();
            _disposed = true;
        }
    }
}

namespace FlexCore.ORM.Providers.EFCore;

using FlexCore.ORM.Core.Interfaces;
using Microsoft.EntityFrameworkCore;
using System;
using System.Collections.Generic;
using System.Threading.Tasks;

/// <summary>
/// Implementazione del provider ORM basato su Entity Framework Core.
/// Questa classe fornisce metodi per interagire con il database tramite EF Core, come aggiungere, aggiornare, eliminare e recuperare entit‚Ä°.
/// </summary>
public class EFCoreOrmProvider : IOrmProvider
{
    // Riferimento al contesto del database (DbContext)
    private readonly DbContext _dbContext;

    /// <summary>
    /// Inizializza una nuova istanza di <see cref="EFCoreOrmProvider"/>.
    /// </summary>
    /// <param name="dbContext">Il contesto del database EF Core.</param>
    /// <exception cref="ArgumentNullException">Lanciato se il <paramref name="dbContext"/> √ã null.</exception>
    public EFCoreOrmProvider(DbContext dbContext)
    {
        _dbContext = dbContext ?? throw new ArgumentNullException(nameof(dbContext));
    }

    /// <summary>
    /// Recupera un'entit‚Ä° per il suo ID in modo asincrono.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entit‚Ä° da recuperare.</typeparam>
    /// <param name="id">L'ID dell'entit‚Ä° da recuperare.</param>
    /// <returns>Un task che rappresenta l'operazione asincrona. Restituisce l'entit‚Ä° se trovata, altrimenti null.</returns>
    public async Task<T?> GetByIdAsync<T>(Guid id) where T : class
    {
        return await _dbContext.Set<T>().FindAsync(id);
    }

    /// <summary>
    /// Recupera tutte le entit‚Ä° di un tipo in modo asincrono.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entit‚Ä° da recuperare.</typeparam>
    /// <returns>Un task che rappresenta l'operazione asincrona. Restituisce una collezione di entit‚Ä°.</returns>
    public async Task<IEnumerable<T>> GetAllAsync<T>() where T : class
    {
        return await _dbContext.Set<T>().AsNoTracking().ToListAsync();
    }

    /// <summary>
    /// Aggiunge una nuova entit‚Ä° al contesto di dati in modo asincrono.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entit‚Ä° da aggiungere.</typeparam>
    /// <param name="entity">L'entit‚Ä° da aggiungere.</param>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public async Task AddAsync<T>(T entity) where T : class
    {
        await _dbContext.Set<T>().AddAsync(entity);
        await _dbContext.SaveChangesAsync();
    }

    /// <summary>
    /// Aggiunge un intervallo di entit‚Ä° al contesto di dati in modo asincrono.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entit‚Ä° da aggiungere.</typeparam>
    /// <param name="entities">Le entit‚Ä° da aggiungere.</param>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public async Task AddRangeAsync<T>(IEnumerable<T> entities) where T : class
    {
        await _dbContext.Set<T>().AddRangeAsync(entities);
        await _dbContext.SaveChangesAsync();
    }

    /// <summary>
    /// Aggiorna un'entit‚Ä° nel contesto di dati in modo asincrono.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entit‚Ä° da aggiornare.</typeparam>
    /// <param name="entity">L'entit‚Ä° da aggiornare.</param>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public async Task UpdateAsync<T>(T entity) where T : class
    {
        _dbContext.Set<T>().Update(entity);
        await _dbContext.SaveChangesAsync();
    }

    /// <summary>
    /// Aggiorna un intervallo di entit‚Ä° nel contesto di dati in modo asincrono.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entit‚Ä° da aggiornare.</typeparam>
    /// <param name="entities">Le entit‚Ä° da aggiornare.</param>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public async Task UpdateRangeAsync<T>(IEnumerable<T> entities) where T : class
    {
        _dbContext.Set<T>().UpdateRange(entities);
        await _dbContext.SaveChangesAsync();
    }

    /// <summary>
    /// Elimina un'entit‚Ä° dal contesto di dati in modo asincrono.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entit‚Ä° da eliminare.</typeparam>
    /// <param name="entity">L'entit‚Ä° da eliminare.</param>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public async Task DeleteAsync<T>(T entity) where T : class
    {
        _dbContext.Set<T>().Remove(entity);
        await _dbContext.SaveChangesAsync();
    }

    /// <summary>
    /// Elimina un intervallo di entit‚Ä° dal contesto di dati in modo asincrono.
    /// </summary>
    /// <typeparam name="T">Il tipo dell'entit‚Ä° da eliminare.</typeparam>
    /// <param name="entities">Le entit‚Ä° da eliminare.</param>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public async Task DeleteRangeAsync<T>(IEnumerable<T> entities) where T : class
    {
        _dbContext.Set<T>().RemoveRange(entities);
        await _dbContext.SaveChangesAsync();
    }

    /// <summary>
    /// Salva tutte le modifiche nel contesto di dati in modo asincrono.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona. Restituisce il numero di entit‚Ä° salvate.</returns>
    public async Task<int> SaveChangesAsync()
    {
        return await _dbContext.SaveChangesAsync();
    }

    /// <summary>
    /// Avvia una transazione asincrona nel contesto di EF Core.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public async Task BeginTransactionAsync()
    {
        await _dbContext.Database.BeginTransactionAsync();
    }

    /// <summary>
    /// Commette la transazione asincrona nel contesto di EF Core.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public async Task CommitTransactionAsync()
    {
        await _dbContext.Database.CommitTransactionAsync();
    }

    /// <summary>
    /// Annulla la transazione asincrona nel contesto di EF Core.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public async Task RollbackTransactionAsync()
    {
        await _dbContext.Database.RollbackTransactionAsync();
    }

    /// <summary>
    /// Rilascia le risorse utilizzate dal provider ORM.
    /// </summary>
    public void Dispose()
    {
        _dbContext?.Dispose();
    }
}

Ôªønamespace FlexCore.ORM.Providers.EFCore;

using Microsoft.EntityFrameworkCore;
using System.Threading.Tasks;
using FlexCore.ORM.Core.Implementations;

/// <summary>
/// Implementazione di UnitOfWork per EF Core. Gestisce la transazione e il salvataggio delle modifiche utilizzando Entity Framework Core.
/// </summary>
public class EFCoreUnitOfWork : UnitOfWorkBase
{
    // Riferimento al contesto del database (DbContext)
    private readonly DbContext _dbContext;

    /// <summary>
    /// Inizializza una nuova istanza di <see cref="EFCoreUnitOfWork"/>.
    /// </summary>
    /// <param name="dbContext">Il contesto del database EF Core.</param>
    public EFCoreUnitOfWork(DbContext dbContext)
    {
        _dbContext = dbContext;
    }

    /// <summary>
    /// Avvia una nuova transazione asincrona nel contesto di EF Core.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public override async Task BeginTransactionAsync()
    {
        await _dbContext.Database.BeginTransactionAsync();
    }

    /// <summary>
    /// Commette la transazione asincrona nel contesto di EF Core.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public override async Task CommitTransactionAsync()
    {
        await _dbContext.Database.CommitTransactionAsync();
    }

    /// <summary>
    /// Annulla la transazione asincrona nel contesto di EF Core.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona.</returns>
    public override async Task RollbackTransactionAsync()
    {
        await _dbContext.Database.RollbackTransactionAsync();
    }

    /// <summary>
    /// Salva le modifiche nel contesto di EF Core in modo asincrono.
    /// </summary>
    /// <returns>Un task che rappresenta l'operazione asincrona, restituendo il numero di entit√† salvate.</returns>
    public override async Task<int> SaveChangesAsync()
    {
        return await _dbContext.SaveChangesAsync();
    }

    /// <summary>
    /// Rilascia le risorse utilizzate dall'istanza di <see cref="EFCoreUnitOfWork"/>.
    /// </summary>
    /// <param name="disposing">Indica se il metodo √® stato chiamato tramite il metodo Dispose o dal finalizzatore.</param>
    protected override void Dispose(bool disposing)
    {
        if (!_disposed)
        {
            if (disposing)
            {
                // Rilascia le risorse gestite (DbContext)
                _dbContext?.Dispose();
            }

            // Rilascia le risorse non gestite (nessuna nel caso di DbContext)
            _disposed = true;
        }
    }
}

