        public void CreateProvider_ShouldThrowIfNotRegistered()
        {
            var factory = new DatabaseProviderFactory();
            Assert.Throws<NotSupportedException>(() =>
                factory.CreateProvider("InvalidProvider", "dummy_connection"));
        }
    }
